import React, { Component } from 'react';
import './App.css';
import pokemons from './data';
import Pokedex from './Pokedex';


class App extends Component {
  constructor() {
    super();
    this.state = {
      type: pokemons,
    };
    this.nextPokemon = this.nextPokemon.bind(this);
  }

  nextPokemon() {
    console.log('clicou');
    this.setState(({ type }) => ({type:{id: 25, name: 'Pikachu', type: 'Electric', averageWeight: {value: '6.0', measurementUnit: 'kg'}, image: 'https://cdn2.bulbagarden.net/upload/b/b2/Spr_5b_025_m.png'}}) )
  }

  render() {
    console.log(this);
    return (
      <div className="App">
      <h1> Pokedex </h1>
      <Pokedex pokemons={this.state.type} />
      <button onClick={this.nextPokemon}>Next Pokemon</button>
    </div>
  );
 } 
}

export default App;
