import React, { Component } from 'react';
import './App.css';
import pokemons from './data';
import Pokedex from './Pokedex';


class App extends Component {
  constructor() {
    super();
    this.state = {
      type: pokemons,
      value: 0,
    };
    this.addValue = this.addValue.bind(this);  
    this.nextPokemon = this.nextPokemon.bind(this);
  }

  addValue() {
    if(this.state.value === 8) {
      this.setState({value: 0});
    } else {
      this.setState(({ value }) => ({value: value += 1}));
    }
    this.nextPokemon(this.state.value);
  }

  nextPokemon(number) {
    this.setState({type: [pokemons[number]]});
    console.log(number)
  }
  //filter((pokemon) => pokemon.type === 'Electric')}

  render() {
    return (
      <div className="App">
      <h1> Pokedex </h1>
      <Pokedex pokemons={this.state.type} />
      <button onClick={this.addValue}>Next Pokemon</button>
    </div>
  );
 } 
}

export default App;
