import React, { Component } from 'react';
import './App.css';
import pokemons from './data';
import Pokedex from './Pokedex';


class App extends Component {
  constructor() {
    super();
    this.state = {
      type: pokemons,
    };
    this.nextPokemon = this.nextPokemon.bind(this);
  }

  nextPokemon() {
    console.log('clicou');
    this.setState(({ type }) => ({type: pokemons[0]}))
  }
  //filter((pokemon) => pokemon.type === 'Electric')}

  render() {
    return (
      <div className="App">
      <h1> Pokedex </h1>
      <Pokedex pokemons={this.state.type} />
      <button onClick={this.nextPokemon}>Next Pokemon</button>
    </div>
  );
 } 
}

export default App;
