import React, { Component } from 'react';
import './App.css';
import pokemons from './data';
import Pokedex from './Pokedex';


class App extends Component {
  constructor() {
    super();
    this.state = {
      type: pokemons,
    }

  }

  render() {
    return (
      <div className="App">
      <h1> Pokedex </h1>
      <Pokedex pokemons={this.state.type} />
      <button>Next Pokemon</button>
    </div>
  );
 } 
}

export default App;
