import React, { Component } from 'react';
import './App.css';
import pokemons from './data';
import Pokedex from './Pokedex';


class App extends Component {
  constructor() {
    super();
    this.state = {
      type: pokemons,
      value: 0,
    };
    this.addValue = this.addValue.bind(this);  
    this.nextPokemon = this.nextPokemon.bind(this);
    this.filterByType = this.filterByType.bind(this);
  }

  addValue() {
    if(this.state.value === 8) {
      this.setState({value: 0});
    } else {
      this.setState(({ value }) => ({value: value += 1}));
    }
    this.nextPokemon(this.state.value);
  }

  nextPokemon(number) {
    this.setState({type: [pokemons[number]]});
  }

  filterByType(model) {
    this.setState(({type}) => ({type: pokemons.filter((pokemon) => pokemon.type === 'Electric')}));
    console.log(this.props)
  }


  render() {
    console.log(this.state.type);
    return (
      <div className="App">
      <h1> Pokedex </h1>
      <Pokedex pokemons={this.state.type} />
      <button onClick={this.addValue}>Next Pokemon</button>
      <button value={'Electric'} onClick={this.filterByType}>Electric</button>
      <button onClick={this.addValue}>Fire</button>
      <button onClick={this.addValue}>Bug</button>
      <button onClick={this.addValue}>Poison</button>
      <button onClick={this.addValue}>Psychic</button>
      <button onClick={this.addValue}>Normal</button>
      <button onClick={this.addValue}>Dragon</button>
      <button onClick={this.addValue}>All Pokemons</button>



    </div>
  );
 } 
}

export default App;
